package main

import (
	"bytes"
	"crypto/tls"
	"io"
	"log"
	"net/http"
	"time"

	"github.com/alexandreStein/gziphandler"

	"golang.org/x/crypto/acme/autocert"
)

var (
	file1 = bytes.NewBufferString("")

	file2 = bytes.NewBufferString("")

	file3 = bytes.NewBufferString("lkhsdvjsdvkls,dvskd,vskd,vlk,s")

	index = `<!DOCTYPE html>
<html>
<head>
<meta charset="UTF-8">
<title>Title of the document</title>
</head>

<body>
Content of the document......<br>

Some content to enable GZIP.<br>

<p>
  
</p>

</body>

<script src="/file1.js"></script>
<script src="/file2.js"></script>
<script src="/file3.js"></script>

</html>`
)

func main() {
	router := http.NewServeMux()

	router.HandleFunc("/file1.js", func(w http.ResponseWriter, r *http.Request) {
		http.ServeContent(w, r, "test file 1", time.Now(), bytes.NewReader(file1.Bytes()))
	})

	router.HandleFunc("/file2.js", func(w http.ResponseWriter, r *http.Request) {
		http.ServeContent(w, r, "test file 2", time.Now(), bytes.NewReader(file2.Bytes()))
	})

	router.HandleFunc("/file3.js", func(w http.ResponseWriter, r *http.Request) {
		http.ServeContent(w, r, "test file 3", time.Now(), bytes.NewReader(file3.Bytes()))
	})

	router.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		ops := &http.PushOptions{
			Header: http.Header{},
		}
		ops.Header.Add("test-header", "OK")
		ops2 := &http.PushOptions{
			Header: http.Header{},
		}
		ops2.Header.Add("Accept-Encoding", "none")
		pusher, ok := w.(http.Pusher)
		if ok { // Push is supported. Try pushing rather than waiting for the browser.
			if err := pusher.Push("/file1.js", ops); err != nil {
				log.Printf("Failed to push: %v", err)
			}
			if err := pusher.Push("/file2.js", ops2); err != nil {
				log.Printf("Failed to push: %v", err)
			}
			if err := pusher.Push("/file3.js", ops); err != nil {
				log.Printf("Failed to push: %v", err)
			}
		} else {
			log.Println("Push not supported:", r.UserAgent())
		}

		io.WriteString(w, index)
	})

	withGz := gziphandler.GzipHandler(router)

	cacheDir := autocert.DirCache("acme")

	m := autocert.Manager{
		Cache:      cacheDir,
		Prompt:     autocert.AcceptTOS,
		HostPolicy: autocert.HostWhitelist("int.gziptester.interlab-net.com"),
	}
	s := &http.Server{
		// Handler: withoutGz,
		Handler:   withGz,
		Addr:      ":4430",
		TLSConfig: &tls.Config{GetCertificate: m.GetCertificate},
	}
	s.ListenAndServeTLS("", "")
}
